import React from 'react';

const AdminDashboard = ({ navigateTo }) => {
  const dashboardCards = [
    {
      title: 'User Management',
      description: 'Manage user accounts, roles, and permissions.',
      link: 'admin-users',
      id: 1
    },
    {
      title: 'Manage User Profile',
      description: 'Update user profiles and personal information.',
      link: 'admin-profiles',
      id: 2
    },
    {
      title: 'Offers Management',
      description: 'Create and manage promotional offers and discounts.',
      link: 'admin-offers',
      id: 3
    },
    {
      title: 'Payment Management',
      description: 'Monitor and manage payment transactions.',
      link: 'admin-orders',
      id: 4
    },
    {
      title: 'Product Management',
      description: 'Add, edit, or remove products from the marketplace.',
      link: 'admin-catalog',
      id: 5
    }
  ];

  return (
    <div style={styles.body}>
      <div style={styles.header}>
        <div style={styles.logo}>
          <span style={styles.logoIcon}>ðŸ›’</span>
          QuickCart
        </div>
      </div>

      <div style={styles.dashboard}>
        <h1 style={styles.dashboardH1}>Admin Dashboard</h1>
        <hr style={styles.dashboardHr} />

        <div style={styles.cards}>
          {dashboardCards.map((card) => (
            <div key={card.id} style={styles.card}>
              <h2 style={styles.cardH2}>{card.title}</h2>
              <p style={styles.cardP}>{card.description}</p>
              <button 
                style={styles.cardButton}
                onMouseOver={(e) => e.target.style.backgroundColor = '#e65b5b'}
                onMouseOut={(e) => e.target.style.backgroundColor = '#ff6b6b'}
                onClick={() => navigateTo(card.link)}
              >
                Go to {card.title}
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const styles = {
  body: {
    fontFamily: 'Arial, sans-serif',
    margin: 0,
    padding: 0,
    backgroundColor: '#f5f5f5',
    minHeight: '100vh'
  },
  header: {
    backgroundColor: '#4a90e2',
    color: 'white',
    padding: '10px 20px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center'
  },
  logo: {
    fontSize: '24px',
    fontWeight: 'bold',
    display: 'flex',
    alignItems: 'center'
  },
  logoIcon: {
    marginRight: '10px'
  },
  nav: {
    display: 'flex',
    alignItems: 'center'
  },
  navLink: {
    color: 'white',
    textDecoration: 'none',
    marginLeft: '20px',
    fontSize: '18px'
  },
  dashboard: {
    maxWidth: '1200px',
    margin: '20px auto',
    textAlign: 'center',
    padding: '0 20px'
  },
  dashboardH1: {
    color: '#333',
    fontSize: '32px',
    marginBottom: '10px'
  },
  dashboardHr: {
    border: '1px solid #ff6b6b',
    width: '50px',
    margin: '0 auto 40px'
  },
  cards: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    gap: '20px'
  },
  card: {
    background: 'white',
    borderRadius: '10px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    width: '250px',
    padding: '20px',
    textAlign: 'left'
  },
  cardH2: {
    color: '#2c3e50',
    fontSize: '20px',
    marginBottom: '10px'
  },
  cardP: {
    color: '#7f8c8d',
    fontSize: '14px',
    marginBottom: '20px'
  },
  cardButton: {
    backgroundColor: '#ff6b6b',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '20px',
    cursor: 'pointer',
    fontSize: '14px',
    transition: 'background-color 0.3s'
  }
};

export default AdminDashboard;
