import React, { useState, useEffect, useCallback } from 'react';
import BagCard from './BagCard';
import './Bags.css';

const Bags = ({ wishlistItems = [], onAddToCart, onToggleWishlist, navigateTo, onViewProduct, cartItems }) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    // const [searchTerm, setSearchTerm] = useState('');

    // --- Fetch Products from API ---
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/products/category/Bags');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const data = await response.json();
                setProducts(data);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };
        fetchProducts();
    }, []);

    // --- Geolocation and Distance Calculation ---
    const getDistanceFromLatLonInKm = useCallback((lat1, lon1, lat2, lon2) => {
        const R = 6371; // Radius of the earth in km
        const deg2rad = (deg) => deg * (Math.PI / 180);
        const dLat = deg2rad(lat2 - lat1);
        const dLon = deg2rad(lon2 - lon1);
        const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        return R * c; // Distance in km
    }, []);

    // --- Geolocation and Distance Calculation ---
    useEffect(() => {
        if (navigator.geolocation && products.length > 0) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const userLat = position.coords.latitude;
                    const userLon = position.coords.longitude;

                    const productsWithDistance = products.map(product => {
                        if (product.lat && product.lon) {
                            const distance = getDistanceFromLatLonInKm(userLat, userLon, product.lat, product.lon);
                            return { ...product, distance: `${distance.toFixed(1)} km away` };
                        }
                        return { ...product, distance: 'Distance N/A' };
                    });
                    setProducts(productsWithDistance);
                },
                (error) => {
                    console.warn(`Geolocation error: ${error.message}.`);
                    const productsWithDefaultDistance = products.map(p => ({...p, distance: 'Within 5km'}));
                    setProducts(productsWithDefaultDistance);
                }
            );
        } else {
            console.warn("Geolocation is not supported by this browser.");
        }
    }, [products, getDistanceFromLatLonInKm]);

    // --- Event Handlers ---
    const handleAddToCart = (product) => {
        if (onAddToCart) {
            onAddToCart(product);
        }
    };

    const handleToggleWishlist = (product) => {
        if (onToggleWishlist) {
            onToggleWishlist(product);
        }
    };

    const isProductInWishlist = (productId) => {
        return wishlistItems.some(item => item._id === productId || item.id === productId);
    };

    // --- Filtered Products ---
    const filteredProducts = products;

    // --- Hero Slider Logic ---
    const [currentSlide, setCurrentSlide] = useState(0);
    const slideImages = [
        "/IMAGES/bags-hero-1.jpg",
        "/IMAGES/bags-hero-2.png",
        "/IMAGES/bags-hero-3.jpg"
    ];

    useEffect(() => {
        const slideInterval = setInterval(() => {
            setCurrentSlide(prev => (prev + 1) % slideImages.length);
        }, 5000);
        return () => clearInterval(slideInterval);
    }, [slideImages.length]);

    return (
        <>
            {/* <Header cartCount={cart.length} wishlistCount={wishlist.length} /> */}
            {/* <Nav /> */}

            <section className="hero" id="hero">
                <div className="slider-container">
                    <div className="bg-slider-track" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
                        {slideImages.map((img, index) => (
                            <div key={index} className={`bg-slide ${index === currentSlide ? 'active' : ''}`} style={{ backgroundImage: `url('${img}')` }}></div>
                        ))}
                    </div>
                </div>
            </section>

            <section id="bags" className="section">
                <div className="container">
                    <div className="section-title">
                        <h2>Bags</h2>
                    </div>
                    {loading && <p>Loading products...</p>}
                    {error && <p>Error: {error}</p>}
                    <div className="product-grid">
                        {filteredProducts.map(product => (
                            <BagCard
                                key={product._id || product.id}
                                product={product}
                                onAddToCart={handleAddToCart}
                                onToggleWishlist={handleToggleWishlist}
                                isWishlisted={isProductInWishlist(product._id || product.id)}
                                cartItems={cartItems}
                                onViewProduct={onViewProduct}
                                navigateTo={navigateTo}
                            />
                        ))}
                    </div>
                </div>
            </section>

            {/* <Footer /> */}
        </>
    );
};

export default Bags;
